import React, { useState, useEffect } from 'react';
import axios from 'axios';


const MovieFull = ({movie}) => {
	
	const [imdbId, setImdbId] = useState("");
	const [omdbMovie, setOmdbMovie] = useState({});
	console.log(movie)
	useEffect(() => {
		const getImdbId = async () => {
		  const id = await axios.get("https://api.themoviedb.org/3/movie/" + movie.id + "/external_ids?api_key=beb7333fa1f8ebe8515528cdc7c5e454");
		  const omdb = await axios.get("http://www.omdbapi.com/?i=" + id.data.imdb_id + "&plot=full&apikey=3893c59c");
		  setImdbId(id.data.imdb_id);
		  setOmdbMovie(omdb.data);
		}
		getImdbId();		
	}, []);

	return (
			<div className='movie'>		
				<h1>{movie.title}</h1>
				<h2>IMDB Rating: {omdbMovie.imdbRating}</h2>
				<p>IMDB ID: {imdbId}</p>
				<img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} alt='movie'></img>
			</div>
	);
};

export default MovieFull;
