import React from 'react';
import {Link} from 'react-router-dom';


const MovieSmall = ({movie}) => {
	return (
			<div className='movie'>		
				<p>{movie.id}</p>
				<li key={movie.id}>
            <Link to={{ pathname: `/movie/${movie.id}`, state: {data: movie.id}  }} >{movie.title}</Link>
         </li>
				<img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} alt='movie'></img>
			</div>
	);
};

export default MovieSmall;
