import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import MovieList from './components/MovieList';
import axios from 'axios';

const App = () => {
  const [cinemaMovies, setCinemaMovies] = useState([]);
  const [netflixMovies, setNetflixMovies] = useState([]);
  const [disneyMovies, setDisneyMovies] = useState([]);
  const [primeMovies, setPrimeMovies] = useState([]);
  const [appleMovies, setAppleMovies] = useState([]);



  const getMovieRequest = async () => {

  const { cinema } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&region=IE&sort_by=release_date.desc&page=1&release_date.gte=2023-04-18&release_date.lte=2023-04-25&with_release_type=2%7C3");

  if( cinema ) { setCinemaMovies(cinema.results); }

  const { netflix } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&sort_by=release_date.desc&page=1&primary_release_date.gte=2023-03-25&primary_release_date.lte=2023-04-25&with_watch_providers=8&watch_region=IE");

  if( netflix ) { setCinemaMovies(netflix.results); }

  const { disney } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&sort_by=release_date.desc&page=1&primary_release_date.gte=2023-03-25&primary_release_date.lte=2023-04-25&with_watch_providers=337&watch_region=IE");

  if( disney ) { setCinemaMovies(disney.results); }

  const { prime } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&sort_by=release_date.desc&page=1&primary_release_date.gte=2023-03-25&primary_release_date.lte=2023-04-25&with_watch_providers=119&watch_region=IE");

  if( prime ) { setCinemaMovies(prime.results); }

  const { apple } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&sort_by=primary_release_date.desc&include_adult=false&include_video=false&primary_release_date.gte=2023-03-25&primary_release_date.lte=2023-04-25&with_watch_providers=2&watch_region=IE");

  if( apple ) { setCinemaMovies(apple.results); }
  };


  useEffect(() => {
    getMovieRequest();
  }, []);



  return (
    <div className='slider'>
      <MovieList movies={cinemaMovies} />
    </div>
  );


};

export default App;
