import React from 'react';
import {Link} from 'react-router-dom';


const MovieSmall = ({movie}) => {
	return (
			<div className='movie'>		
				<li key={movie.id}>
            <Link to={`/movie/${movie.id}`} state={{ movie: {movie} }} ><img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} alt='movie'></img></Link>
         </li>
				{/*<img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} alt='movie'></img>*/}
			</div>
	);
};

export default MovieSmall;
