import React, { useState, useEffect } from 'react';
import axios from 'axios';
// import Movie from './Movie.js';


const MovieFull = ({movie}) => {
	
	const [imdbId, setImdbId] = useState("Bollocks!");
	//const [omdbMovie, setOmdbMovie] = useState("Wanker");

	// const getImdbId = async () => {
	//   const { data } = await axios.get("https://api.themoviedb.org/3/movie/464768/external_ids?api_key=beb7333fa1f8ebe8515528cdc7c5e454");
	//   if( data ) { setImdbId(data); console.log(data); }
	// }

  useEffect(() => {
    const fetchData = async () =>{
      try {
        const {data} = await axios.get("https://api.themoviedb.org/3/movie/" + movie.id + "/external_ids?api_key=beb7333fa1f8ebe8515528cdc7c5e454");
        setImdbId(data.imdb_id);
      } catch (error) {
        console.error(error.message);
      }
      console.log(movie);
    }
    fetchData();
  }, []);

	return (
			<div className='movie'>		
				<h1>{movie.title}</h1>
				<p>Imdb: {imdbId}</p>
				<img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} alt='movie'></img>
			</div>
	);

	// useEffect(() => {
	//   getImdbId();
	// });

};

export default MovieFull;
