import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import MovieList from './components/MovieList';
import axios from 'axios';

const App = () => {
  const [cinemaMovies, setCinemaMovies] = useState([]);
  const [netflixMovies, setNetflixMovies] = useState([]);
  const [disneyMovies, setDisneyMovies] = useState([]);
  const [primeMovies, setPrimeMovies] = useState([]);
  const [appleMovies, setAppleMovies] = useState([]);



  const getMovieRequest = async () => {

  const { data: cinema } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&region=IE&sort_by=release_date.desc&page=1&release_date.gte=2023-04-18&release_date.lte=2023-04-25&with_release_type=2%7C3");

  if( cinema ) { setCinemaMovies(cinema.results); console.log(cinemaMovies); }

  const { data: netflix } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&sort_by=release_date.desc&page=1&primary_release_date.gte=2023-03-25&primary_release_date.lte=2023-04-25&with_watch_providers=8&watch_region=IE");

  if( netflix ) { setNetflixMovies(netflix.results); }

  const { data: disney } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&sort_by=release_date.desc&page=1&primary_release_date.gte=2023-03-25&primary_release_date.lte=2023-04-25&with_watch_providers=337&watch_region=IE");

  if( disney ) { setDisneyMovies(disney.results); }

  const { data: prime } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&sort_by=release_date.desc&page=1&primary_release_date.gte=2023-03-25&primary_release_date.lte=2023-04-25&with_watch_providers=119&watch_region=IE");

  if( prime ) { setPrimeMovies(prime.results); }

  const { data: apple } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&sort_by=primary_release_date.desc&include_adult=false&include_video=false&primary_release_date.gte=2023-03-25&primary_release_date.lte=2023-04-25&with_watch_providers=2&watch_region=IE");

  if( apple ) { setAppleMovies(apple.results); }
  };


  useEffect(() => {
    getMovieRequest();
  }, []);



  return (
    <div className='slider'>
      <h2 className="category_heading">Cinema</h2>
      <MovieList movies={cinemaMovies} />
      <h2 className="category_heading">Netflix</h2>
      <MovieList movies={netflixMovies} />
      <h2 className="category_heading">Disney+</h2>
      <MovieList movies={disneyMovies} />
      <h2 className="category_heading">Amazon Prime</h2>
      <MovieList movies={primeMovies} />
      <h2 className="category_heading">Apple TV</h2>
      <MovieList movies={appleMovies} />
    </div>
  );


};

export default App;
