import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';


const MovieSmall = ({movie}) => {
		return (
			<div className='movie'>		
            <Link to={`/movie/${movie.id}`} state={{ movie: {movie} }} ><img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} alt={`Poster of ${movie.title}`}></img></Link>
			</div>
	);
};

export default MovieSmall;
