import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import MovieList from './components/MovieList';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './views/Home'
import Cinema from './views/Cinema'
import Netflix from './views/Netflix'
import Disney from './views/Disney'
import Prime from './views/Prime'
import Apple from './views/Apple'
import Movie from './views/Movie'
import Navbar from './components/Navbar'

const App = () => {
  const [cinemaMovies, setCinemaMovies] = useState([]);
  const [netflixMovies, setNetflixMovies] = useState([]);
  const [disneyMovies, setDisneyMovies] = useState([]);
  const [primeMovies, setPrimeMovies] = useState([]);
  const [appleMovies, setAppleMovies] = useState([]);



  const getMovieRequest = async () => {


  function getDates( days )
  {
    var currentDate = new Date();

    var currentYear = currentDate.getFullYear();
    var currentMonth = String(currentDate.getMonth() + 1).padStart(2, '0');
    var currentDay = String(currentDate.getDate()).padStart(2, '0');
    var formattedCurrent = `${currentYear}-${currentMonth}-${currentDay}`; 

    var priorDate = new Date(new Date().setDate(currentDate.getDate() - days));
    var priorYear = priorDate.getFullYear();
    var priorMonth = String(priorDate.getMonth() + 1).padStart(2, '0');
    var priorDay = String(priorDate.getDate()).padStart(2, '0');
    var formattedPrior = `${priorYear}-${priorMonth}-${priorDay}`;
    return { today: formattedCurrent, prior: formattedPrior }    
  }

  var dates = getDates(365);

  console.log("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&sort_by=primary_release_date.desc&page=1&primary_release_date.gte=" + dates.prior + "&primary_release_date.lte=" + dates.today + "&with_watch_providers=8&watch_region=IE");


  const getCinema = async (dates) => {
    let { data: cinema } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&region=IE&sort_by=release_date.desc&page=1&release_date.gte=" + dates.prior + "&release_date.lte=" + dates.today + "&with_release_type=2%7C3");
    if( cinema ) { return(cinema.results); }
  }


  function getPlatformID(platform)
  {
    switch (platform) 
    {
      case 'netflix':
        return 8;
        break;
      case 'disney':
        return 337;
        break;
      case 'prime':
        return 119;
        break;
      case 'apple':
        return 2;
        break;
    }
  }

  const getStreaming = async (dates, platform) => {


    let { data: streaming } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&sort_by=primary_release_date.desc&page=1&primary_release_date.gte=" + dates.prior + "&primary_release_date.lte=" + dates.today + "&with_watch_providers=" + getPlatformID(platform) + "&watch_region=IE");
    if( streaming ) { return(streaming.results); }
  }

  const cinemaResults = await getCinema(dates);
  setCinemaMovies(cinemaResults);

  const netflixResults = await getStreaming(dates, 'netflix');
  setNetflixMovies(netflixResults);

  const disneyResults = await getStreaming(dates, 'disney');
  setDisneyMovies(disneyResults);

  const primeResults = await getStreaming(dates, 'prime');
  setPrimeMovies(primeResults);

  const appleResults = await getStreaming(dates, 'apple');
  setAppleMovies(appleResults);

  // const { data: cinema } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&region=IE&sort_by=release_date.desc&page=1&release_date.gte=" + dates.prior + "&release_date.lte=" + dates.today + "&with_release_type=2%7C3");

  // if( cinema ) { setCinemaMovies(cinema.results); console.log(cinema.results) }

  // const { data: netflix } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&sort_by=primary_release_date.desc&page=1&primary_release_date.gte=2023-03-25&primary_release_date.lte=2023-04-25&with_watch_providers=8&watch_region=IE");

  // if( netflix ) { setNetflixMovies(netflix.results); }

  // const { data: disney } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&sort_by=primary_release_date.desc&page=1&primary_release_date.gte=2023-03-25&primary_release_date.lte=2023-04-25&with_watch_providers=337&watch_region=IE");

  // if( disney ) { setDisneyMovies(disney.results); }

  // const { data: prime } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&sort_by=primary_release_date.desc&page=1&primary_release_date.gte=2023-03-25&primary_release_date.lte=2023-04-25&with_watch_providers=119&watch_region=IE");

  // if( prime ) { setPrimeMovies(prime.results); }

  // const { data: apple } = await axios.get("https://api.themoviedb.org/3/discover/movie?api_key=beb7333fa1f8ebe8515528cdc7c5e454&sort_by=primary_release_date.desc&include_adult=false&include_video=false&primary_release_date.gte=2023-03-25&primary_release_date.lte=2023-04-25&with_watch_providers=2&watch_region=IE");

  // if( apple ) { setAppleMovies(apple.results); }
  };

//Call to get IMDB id  
//https://api.themoviedb.org/3/movie/464768/external_ids?api_key=beb7333fa1f8ebe8515528cdc7c5e454

  useEffect(() => {
    getMovieRequest();
  }, []);



  return (
    <Router>
      <div>
        <Navbar />
          <Routes>
            <Route exact path="/" element={<Home cinema={cinemaMovies} netflix={netflixMovies} disney={disneyMovies} 
                                                 prime={primeMovies} apple={appleMovies} />} />
            <Route path="/cinema" element={<Cinema cinema={cinemaMovies} />} />
            <Route path="/netflix" element={<Netflix netflix={netflixMovies} />} />
            <Route path="/disney" element={<Disney disney={disneyMovies} />} />
            <Route path="/prime" element={<Prime prime={primeMovies} />} />
            <Route path="/apple" element={<Apple apple={appleMovies} />} />
            <Route path="/movie/:id" element={<Movie />} />
          </Routes>
      </div>
    </Router>
  );


};

export default App;
