// import MovieList from '../components/MovieList';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MovieFull from '../components/MovieFull';
import { useLocation } from "react-router-dom";

function Movie() {

const location = useLocation();
const { movie } = location.state;

  const [imdbId, setImdbId] = useState("Nathan");
  const [omdbMovie, setOmdbMovie] = useState({});

  const getImdbId = async () => {
    const { data } = await axios.get("https://api.themoviedb.org/3/movie/464768/external_ids?api_key=beb7333fa1f8ebe8515528cdc7c5e454");
    if( data ) { setImdbId(data); }
  }


  return (
    <div>
      <MovieFull movie={movie.movie} imdbId={imdbId} /> 
      }
    </div>
  )

  useEffect(() => {
    getImdbId();
  });
}



export default Movie;
