import React, { useState, useEffect } from 'react';
import axios from 'axios';


const MovieFull = ({movie}) => {
	
	const [imdbId, setImdbId] = useState("Shit");
	const [omdbMovie, setOmdbMovie] = useState({});

	useEffect(() => {
		const getImdbId = async () => {
		  const { data: id } = await axios.get("https://api.themoviedb.org/3/movie/" + movie.id + "/external_ids?api_key=beb7333fa1f8ebe8515528cdc7c5e454");
		  setImdbId(id.imdb_id);
		  const { omdb } = await axios.get("http://www.omdbapi.com/?i=" + imdbId + "&plot=full&apikey=3893c59c");
		  setImdbId(omdb);
		  console.log(omdb);
		}
		getImdbId();		
	}, [imdbId]);


	useEffect(() => {
		if( imdbId ) {
			const getImdbId = async () => {
			  const { omdb } = await axios.get("http://www.omdbapi.com/?i=" + imdbId + "&plot=full&apikey=3893c59c");
			  setImdbId(omdb);
			  console.log(imdbId);			
			}
		}	
	}, [imdbId]);


	return (
			<div className='movie'>		
				<h1>{movie.title}</h1>
				<p>Imdb: {imdbId}</p>
				<img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} alt='movie'></img>
			</div>
	);
};

export default MovieFull;
