import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';


const MovieSmall = ({movie}) => {

	const [imdbId, setImdbId] = useState("Nathan");
	const [omdbMovie, setOmdbMovie] = useState({});

	const getImdbId = async () => {
	  const { data } = await axios.get("https://api.themoviedb.org/3/movie/464768/external_ids?api_key=beb7333fa1f8ebe8515528cdc7c5e454");
	  if( data ) { setImdbId(data.imdb_id); console.log(data.imdb_id); }
	}

	return (
			<div className='movie'>		
            <Link to={`/movie/${movie.id}`} state={{ movie: {movie}, imdbId: {imdbId} }} onClick={getImdbId} ><img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} alt='movie'></img></Link>

			</div>
	);
};

export default MovieSmall;
