import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';
import {db} from '../db.js';


const MovieSmall = ({movie}) => {
	

  const addItemToDb = async movie => {
    let { data: image } = await axios.get(`http://image.tmdb.org/t/p/w200${movie.poster_path}`, {responseType: 'blob'});
    let id = movie.id
    let title = movie.title
	  await db.items.add({ 
	    id,
	    title,
	    image
	  })
    
  }

  const removeItemFromDb = async movie => {
    await db.items.delete(movie.id)
  } 


	return (
		
		<div className='movie'>
		  <button onClick={() => addItemToDb(movie)}>Add Item To DB</button> 
          <button onClick={() => removeItemFromDb(movie)}>Remove Item From DB</button>		
      	<Link to={`/movie/${movie.id}`} state={{ movie: {movie} }} >
      	<img src={"https://image.tmdb.org/t/p/w200" + movie.poster_path} alt={`Poster of ${movie.title}`}></img>
      </Link>
    	<button className="add_button">Add to Watchlist</button>  
		</div>
	);
};

export default MovieSmall;
